CREATE OR REPLACE FUNCTION obter_nome_pf_oculto_senhas ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(255);
ie_nome_oculto_w	varchar(1) := 'N';			
 

BEGIN 
 
ie_nome_oculto_w  := obter_param_Usuario(10021, 130, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_nome_oculto_w );
 
--ie_nome_oculto_w := 'S'; 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then	 
	 
	if ( ie_opcao_p = 'O') and ( ie_nome_oculto_w = 'S') then	   
		ds_retorno_w := substr(obter_nome_pf_oculta(cd_pessoa_fisica_p, obter_perfil_ativo, obter_usuario_ativo, null),1,200);
	elsif (ie_opcao_p = 'O') and (ie_nome_oculto_w = 'N') then	   
		ds_retorno_w := SUBSTR(OBTER_NOME_PF(cd_pessoa_fisica_p), 0, 255);
	elsif ( ie_opcao_p = 'P') then 
		ds_retorno_w := SUBSTR(OBTER_NOME_PF(cd_pessoa_fisica_p), 0, 255);
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
