CREATE OR REPLACE FUNCTION obter_nome_pf_particip_biob ( nr_sequencia_tubo_p bigint, ie_tipo_retorno text) RETURNS varchar AS $body$
DECLARE

 
/* 
ie_tipo_retorno 
CD = Retornar o código de pessoa fisica do participante que está o ocupando a posição do mapa 
NM = Retornar o nome do participante que está o ocupando a posição do mapa 
*/
 
			 
 
retorno_funct_w	pessoa_fisica.nm_pessoa_fisica%type := '';

cd_pessoa_fisica_w 	pessoa_fisica.cd_pessoa_fisica%type;
nr_seq_superior_w	biob_coleta_tubo.nr_seq_superior%type;
nr_seq_coleta_w		biob_coleta_tubo.nr_seq_coleta%type;

 

BEGIN 
 
if ((nr_sequencia_tubo_p IS NOT NULL AND nr_sequencia_tubo_p::text <> '') and nr_sequencia_tubo_p > 0) then 
 
	select	max(nr_seq_superior), 
		max(nr_seq_coleta) 
	into STRICT	nr_seq_superior_w, 
		nr_seq_coleta_w 
	from	biob_coleta_tubo 
	where	nr_seq_mapa_loc = nr_sequencia_tubo_p;
 
	if (nr_seq_coleta_w > 0) then 
		 
		select	d.cd_pessoa_fisica 
		into STRICT	cd_pessoa_fisica_w		 
		from	biob_doador_coleta c, 
			biob_doador d 
		where	c.nr_seq_doador = d.nr_sequencia 
		and	c.nr_sequencia = nr_seq_coleta_w;
		 
	elsif (nr_seq_superior_w > 0) then 
	 
		/*select 	obter_nome_Pf_particip_biob(nr_seq_mapa_loc, 'CD') 
		INTO	cd_pessoa_fisica_w 
		from	biob_coleta_tubo 
		where	nr_sequencia = nr_seq_superior_w;*/
 
		 
		select 	max(nr_seq_coleta) 
		into STRICT	nr_seq_coleta_w 
		from	biob_coleta_tubo 
		where	nr_sequencia = nr_seq_superior_w;
		 
		select	max(d.cd_pessoa_fisica) 
		into STRICT	cd_pessoa_fisica_w		 
		from	biob_doador_coleta c, 
			biob_doador d 
		where	c.nr_seq_doador = d.nr_sequencia 
		and	c.nr_sequencia = nr_seq_coleta_w;
 
	end if;
	 
	if (ie_tipo_retorno = 'CD') then 
	 
		retorno_funct_w := cd_pessoa_fisica_w;
		 
	elsif (ie_tipo_retorno = 'NM') then 
	 
		select	obter_nome_pessoa_fisica(cd_pessoa_fisica_w,'') 
		into STRICT	retorno_funct_w 
		;
		 
	end if;
	 
end if;
 
return retorno_funct_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
