CREATE OR REPLACE FUNCTION obter_nome_pf_pj_wsi ( cd_pessoa_fisica_p text, cd_cgc_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		pessoa_juridica.ds_razao_social%type;


BEGIN
ds_retorno_w		:= '';
if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	begin
	select ds_razao_social
	into STRICT	 ds_retorno_w
	from	 pessoa_juridica
	where	 cd_cgc	= cd_cgc_p;
	end;
elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
		select  substr(nm_pessoa_fisica,1,60)
		into STRICT	ds_retorno_w
		from 	table(search_names_dev(null,cd_pessoa_fisica_p,null,null,null, cd_estabelecimento_p));
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
