CREATE OR REPLACE FUNCTION obter_nome_pf_pj ( CD_PESSOA_FISICA_P text, CD_CGC_P text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		pessoa_juridica.ds_razao_social%type;


BEGIN
ds_retorno_w		:= '';
if (CD_CGC_P IS NOT NULL AND CD_CGC_P::text <> '') then
	begin
	select DS_RAZAO_SOCIAL
	into STRICT	 ds_retorno_w
	from	 PESSOA_JURIDICA
	where	 CD_CGC	= CD_CGC_P;
	end;
elsif (CD_PESSOA_FISICA_P IS NOT NULL AND CD_PESSOA_FISICA_P::text <> '') then
	begin
    ds_retorno_w := SUBSTR(OBTER_NOME_PF(CD_PESSOA_FISICA_P), 0, 60);
	end;
end if;

RETURN ds_retorno_w;
END;
$body$
