SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nome_pf_quimio ( cd_pessoa_fisica_p text, cd_perfil_p bigint, nr_atendimento_p bigint, param_220_p text) RETURNS varchar AS $body$
DECLARE



dt_entrada_w		timestamp;
cd_pessoa_fisica_w	varchar(10);
nm_pessoa_w			varchar(255);
qt_reg_w			bigint;

c01 CURSOR FOR
	SELECT	nm_pessoa_fisica
	from	pessoa_fisica_alteracao
	where	cd_pessoa_fisica = cd_pessoa_fisica_w
	and		dt_atualizacao > dt_entrada_w
	order by dt_atualizacao;


BEGIN

if (param_220_p = 'S') then

	select	max(dt_entrada),
			max(cd_pessoa_fisica)
	into STRICT	dt_entrada_w,
			cd_pessoa_fisica_w
	from	atendimento_paciente
	where	nr_atendimento = nr_atendimento_p;

	select	count(1)
	into STRICT	qt_reg_w
	from	pessoa_fisica_alteracao
	where	cd_pessoa_fisica = cd_pessoa_fisica_w
	and		dt_atualizacao > dt_entrada_w;

	if (qt_reg_w > 0) then
		open c01;
		loop
		fetch c01 into
			nm_pessoa_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			if ((trim(both nm_pessoa_w) IS NOT NULL AND (trim(both nm_pessoa_w))::text <> '')) then
				exit;
			end if;
			end;
		end loop;
		close c01;
	end if;

end if;

if (coalesce(nm_pessoa_w::text, '') = '') or (nm_pessoa_w = '') then
	begin
	nm_pessoa_w := substr(obter_nome_pf_oculta(cd_pessoa_fisica_p, cd_perfil_p, wheb_usuario_pck.get_nm_usuario, null),1,255);
	end;
end if;

return substr(nm_pessoa_w, 1, 60);

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
