DECLARE



nr_seq_vaga_w   	bigint;
cd_pessoa_reserva_w  	varchar(20);

BEGIN


select  coalesce(min(nr_sequencia),0)
into STRICT	nr_seq_vaga_w
from	fila_espera_reserva
where	ie_status  = 'R'
and	cd_unidade_basica = cd_unidade_basica_p
