CREATE OR REPLACE FUNCTION obter_nome_prescr_laudo ( nr_prescricao_p bigint, nr_atendimento_p text) RETURNS varchar AS $body$
DECLARE

 
ie_recem_nato_w		varchar(1);
nm_recem_nato_w		varchar(100);
nm_pessoa_fisica_w	varchar(100);

ds_retorno_w		varchar(255);

 

BEGIN 
 
 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then 
 
	select 	max(coalesce(ie_recem_nato,'N')), 
		max(obter_nome_pf(cd_recem_nato)), 
		max(obter_nome_pf(cd_pessoa_fisica)) 
	into STRICT	ie_recem_nato_w, 
		nm_recem_nato_w, 
		nm_pessoa_fisica_w 
	from	prescr_medica 
	where 	nr_prescricao = nr_prescricao_p;
	 
else 
	select	max(substr(obter_nome_pf(b.cd_pessoa_fisica),1,100)) 
	into STRICT	nm_pessoa_fisica_w 
	from	pessoa_fisica b, 
		atendimento_paciente a 
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
	and	a.nr_atendimento = nr_atendimento_p;
	 
	ie_recem_nato_w := 'N';
	nm_recem_nato_w := null;
 
end if;	
 
 
if (ie_recem_nato_w = 'N') then 
	begin 
	ds_retorno_w := nm_pessoa_fisica_w;
	end;
elsif (nm_recem_nato_w IS NOT NULL AND nm_recem_nato_w::text <> '') then 
	begin 
	ds_retorno_w := wheb_mensagem_pck.get_texto(308629) || ': ' || nm_recem_nato_w; -- RN 
	end;
else 
	ds_retorno_w := wheb_mensagem_pck.get_texto(308629) || ' ' || wheb_mensagem_pck.get_texto(308630) || ': ' || nm_pessoa_fisica_w; -- RN de 
end if;
 
 
return	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
