CREATE OR REPLACE FUNCTION obter_nome_protocolo ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_protocolo_w	PROTOCOLO_INT_PACIENTE.DS_TRATAMENTO%type;


BEGIN

select MAX(D.ds_tratamento)
into STRICT	ds_protocolo_w
from med_avaliacao_result   A,
     med_item_avaliar       B,
     med_tipo_avaliacao     C,
     protocolo_int_paciente D
where A.nr_seq_item = B.nr_sequencia
and B.nr_seq_tipo = C.nr_sequencia
and qt_resultado  = D.nr_sequencia
and UPPER(B.ds_regra) LIKE '%PROTOCOLO_INT_PACIENTE%'
and A.nr_seq_avaliacao = nr_sequencia_p;

return	ds_protocolo_w;


end	;
