CREATE OR REPLACE FUNCTION obter_nome_razao_social ( CD_CGC_P text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
ds_razao_social_w	varchar(80);
cd_cgc_w		varchar(80);


BEGIN
if (CD_CGC_P IS NOT NULL AND CD_CGC_P::text <> '') then

	select	max(cd_cgc),
		max(ds_razao_social)
	into STRICT	cd_cgc_w,
		ds_razao_social_w
	from	pessoa_juridica
	where	cd_cgc	= cd_cgc_p;

	if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
		ds_retorno_w := ds_razao_social_w;
	end if;

end if;

RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
