CREATE OR REPLACE FUNCTION obter_nome_sbis ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
nm_pf_w				varchar(255);
ie_sbis_w			varchar(1);
cd_pessoa_fisica_w	varchar(10);
nm_pessoa_oculta_w	varchar(255);


BEGIN 
ie_sbis_w := obter_param_usuario(0, 220, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_sbis_w);
 
cd_pessoa_fisica_w := obter_pessoa_atendimento(nr_atendimento_p, 'C');
nm_pessoa_oculta_w := obter_Nome_PF_Oculta(cd_pessoa_fisica_w,obter_perfil_ativo,null);
 
if (ie_sbis_w = 'S') then 
 
	select	max(substr(PEP_obter_nome_sbis(nr_atendimento_p,nm_pessoa_oculta_w ),1,80)) 
	into STRICT	nm_pf_w 
	;
	 
end if;
 
return	coalesce(nm_pf_w,nm_pessoa_oculta_w);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
