CREATE OR REPLACE FUNCTION obter_nome_social_pf ( CD_PESSOA_FISICA_P text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);


BEGIN

if (CD_PESSOA_FISICA_P IS NOT NULL AND CD_PESSOA_FISICA_P::text <> '') then
	select	max(coalesce(substr(NM_SOCIAL,1,100),NM_PESSOA_FISICA))
	into STRICT	ds_retorno_w
	from	PESSOA_FISICA a
	where	CD_PESSOA_FISICA	= CD_PESSOA_FISICA_P;

end if;

RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
