CREATE OR REPLACE FUNCTION obter_nome_tipo_sol (ie_tipo_sol_p text) RETURNS varchar AS $body$
DECLARE


ds_tipo_w	varchar(255);


BEGIN

if (ie_tipo_sol_p	= '1') then
	ds_tipo_w	:= obter_desc_expressao(492860);--'Solução intermitente (medicamentos de horário ou soluções não-contínuas cujo tempo de infusão é menor que o intervalo entre as doses)';
elsif (ie_tipo_sol_p	= '2') then
	ds_tipo_w	:= obter_desc_expressao(492861);--'Solução em esquema alternado (quando há períodos do dia com e sem etapas a serem infundidas)';
elsif (ie_tipo_sol_p	= '3') then
	ds_tipo_w	:= obter_desc_expressao(492862);--'Solução contínua (etapas ligadas, sem espaço de tempo entre elas)';
elsif (ie_tipo_sol_p	= '4') then
	ds_tipo_w	:= obter_desc_expressao(492863);--'Solução especial (com velocidade de infusão variável)';
elsif (ie_tipo_sol_p	= '5') then
	ds_tipo_w	:= obter_desc_expressao(492864);--'Solução para analgesia controlada pelo paciente (PCA)';
end if;

return	ds_tipo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
