CREATE OR REPLACE FUNCTION obter_nome_usuario_adicional ( ds_usuarios_p text) RETURNS varchar AS $body$
DECLARE


ds_codigo_w	varchar(2000);
cd_posicao_w	bigint;
nm_usuario_w	varchar(255);
ds_usuario_w	varchar(40);
ds_retorno_w	varchar(2000);

BEGIN

ds_codigo_w	:= substr(ds_usuarios_p,1,2000);

if (ds_codigo_w IS NOT NULL AND ds_codigo_w::text <> '') then
	cd_posicao_w	:= coalesce(position(',' in ds_codigo_w),0);
	while(cd_posicao_w	<> 0) loop
		begin
		nm_usuario_w	:= to_char(coalesce(trim(both substr(ds_codigo_w,1,cd_posicao_w-1)),'0'));

		select	substr(obter_nome_usuario(nm_usuario_w),1,40)
		into STRICT	ds_usuario_w
		;

		ds_retorno_w	:= substr(ds_usuario_w || ', ' || ds_retorno_w,1,2000);

		ds_codigo_w	:= substr(ds_codigo_w, (cd_posicao_w)::numeric  +1, length(ds_codigo_w));

		cd_posicao_w	:= position(',' in ds_codigo_w);

		end;
	end loop;

end if;

return	substr(ds_retorno_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
