CREATE OR REPLACE FUNCTION obter_nome_usuario_exec2 ( nr_ordem_servico_p bigint, nr_seq_estagio_p bigint, nm_usuario_exec_p text) RETURNS varchar AS $body$
DECLARE


nm_usuario_w			varchar(80);
ds_resultado_w			varchar(80);
nm_usuario_exec_w		varchar(15);
ie_desenv_w			varchar(1);
ie_suporte_w			varchar(1);
nr_seq_exec_w			bigint;
nr_seq_estagio_w		bigint;
cd_perfil_ativo_w		integer	:= obter_perfil_ativo;
ie_tipo_executor_w		man_tipo_executor.ie_tipo_executor%type;
nr_seq_exec_dt_exec_w		bigint;
nr_seq_exec_dt_sexec_w		bigint;
nr_seq_exec_sdt_exec_w		bigint;
nr_seq_exec_sdt_sexec_w		bigint;
nm_usuario_dt_exec_w		varchar(80);
nm_usuario_dt_sexec_w		varchar(80);
nm_usuario_sdt_exec_w		varchar(80);
nm_usuario_sdt_sexec_w		varchar(80);


C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.dt_fim_execucao,
		a.nr_seq_tipo_exec,
		nm_usuario_exec
	from	man_ordem_servico_exec	a
	where	a.nr_seq_ordem 	= nr_ordem_servico_p
	and  	exists (SELECT	1
			from  	man_estagio_usuario	d
			where   d.nm_usuario_acao 	= a.nm_usuario_exec
			and	CASE WHEN coalesce(d.cd_perfil,0)=0 THEN coalesce(cd_perfil_ativo_w,0)  ELSE d.cd_perfil END 	= CASE WHEN coalesce(cd_perfil_ativo_w,0)=0 THEN CASE WHEN coalesce(d.cd_perfil,0)=0 THEN coalesce(cd_perfil_ativo_w,0)  ELSE d.cd_perfil END   ELSE cd_perfil_ativo_w END )
	and (exists (select	1
			from	usuario_grupo_des	x
			where	x.nm_usuario_grupo	= a.nm_usuario_exec) or ie_desenv_w = 'N')
	and (exists (select	1
			from	usuario_grupo_sup	x
			where	x.nm_usuario_grupo	= a.nm_usuario_exec) or ie_suporte_w = 'N')
	order by
		a.nr_sequencia;

vetC01_w	C01%rowtype;


BEGIN
if (nr_ordem_servico_p IS NOT NULL AND nr_ordem_servico_p::text <> '') then
	begin
	/*select	max(nr_seq_estagio)
	into	nr_seq_estagio_w
	from	man_ordem_servico
	where	nr_sequencia	= nr_ordem_servico_p;*/
	select	coalesce(max(ie_desenv),'N'),
		coalesce(max(ie_suporte),'N')
	into STRICT	ie_desenv_w,
		ie_suporte_w
	from	man_estagio_processo
	where	nr_sequencia	= nr_seq_estagio_p;

	/*begin
	select	nm_usuario_exec
	into	nm_usuario_exec_w
	from	man_ordem_servico
	where	nr_sequencia 	= nr_ordem_servico_p;
	exception
	when others then
		nm_usuario_exec_w := wheb_mensagem_pck.get_texto(308632); -- Não informado
	end;*/
	nm_usuario_exec_w	:= nm_usuario_exec_p;

	nm_usuario_exec_w := coalesce(nm_usuario_exec_w, wheb_mensagem_pck.get_texto(308632)); -- Não informado
	nr_seq_exec_w	:= 0;

	open C01;
	loop
	fetch C01 into
		vetC01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(vetC01_w.dt_fim_execucao::text, '') = '') then
			if (vetC01_w.nr_seq_tipo_exec IS NOT NULL AND vetC01_w.nr_seq_tipo_exec::text <> '') then
				select	max(a.ie_tipo_executor)
				into STRICT	ie_tipo_executor_w
				from	man_tipo_executor	a
				where	a.nr_sequencia	= vetC01_w.nr_seq_tipo_exec;

				if (ie_tipo_executor_w = '1') then
					nr_seq_exec_sdt_exec_w	:= vetC01_w.nr_sequencia;
					nm_usuario_sdt_exec_w	:= vetC01_w.nm_usuario_exec;
				end if;
			else
				nr_seq_exec_sdt_sexec_w	:= vetC01_w.nr_sequencia;
				nm_usuario_sdt_sexec_w	:= vetC01_w.nm_usuario_exec;
			end if;
		else
			if (vetC01_w.nr_seq_tipo_exec IS NOT NULL AND vetC01_w.nr_seq_tipo_exec::text <> '') then
				select	max(a.ie_tipo_executor)
				into STRICT	ie_tipo_executor_w
				from	man_tipo_executor	a
				where	a.nr_sequencia	= vetC01_w.nr_seq_tipo_exec;

				if (ie_tipo_executor_w = '1') then
					nr_seq_exec_dt_exec_w	:= vetC01_w.nr_sequencia;
					nm_usuario_dt_exec_w	:= vetC01_w.nm_usuario_exec;
				end if;
			else
				nr_seq_exec_dt_sexec_w	:= vetC01_w.nr_sequencia;
				nm_usuario_dt_sexec_w	:= vetC01_w.nm_usuario_exec;
			end if;
		end if;
		end;
	end loop;
	close C01;

	if (nr_seq_exec_dt_exec_w IS NOT NULL AND nr_seq_exec_dt_exec_w::text <> '') then
		nr_seq_exec_w	:= nr_seq_exec_dt_exec_w;
		nm_usuario_w	:= nm_usuario_dt_exec_w;
	elsif (nr_seq_exec_dt_sexec_w IS NOT NULL AND nr_seq_exec_dt_sexec_w::text <> '') then
		nr_seq_exec_w	:= nr_seq_exec_dt_sexec_w;
		nm_usuario_w	:= nm_usuario_dt_sexec_w;
	elsif (nr_seq_exec_sdt_exec_w IS NOT NULL AND nr_seq_exec_sdt_exec_w::text <> '') then
		nr_seq_exec_w	:= nr_seq_exec_sdt_exec_w;
		nm_usuario_w	:= nm_usuario_sdt_exec_w;
	elsif (nr_seq_exec_sdt_sexec_w IS NOT NULL AND nr_seq_exec_sdt_sexec_w::text <> '') then
		nr_seq_exec_w	:= nr_seq_exec_sdt_sexec_w;
		nm_usuario_w	:= nm_usuario_sdt_sexec_w;
	end if;

	/*begin
	select	max(a.nr_sequencia)
	into	nr_seq_exec_w
	from	man_tipo_executor c,
		man_ordem_servico_exec a
	where	a.nr_seq_tipo_exec	= c.nr_sequencia
	and	c.ie_tipo_executor 	= '1'
	and	a.nr_seq_ordem 		= nr_ordem_servico_p
	and	a.dt_fim_execucao is null
	--and	rownum < 2
	and  	exists (select	1
			from  	man_estagio_usuario d
			where   d.nm_usuario_acao 	= a.nm_usuario_exec
			and	decode(nvl(d.cd_perfil,0),0,nvl(cd_perfil_ativo_w,0),d.cd_perfil) =
			decode(nvl(cd_perfil_ativo_w,0),0,decode(nvl(d.cd_perfil,0),0,nvl(cd_perfil_ativo_w,0),d.cd_perfil),cd_perfil_ativo_w))
	and	(exists	(select	1
			from	usuario_grupo_des x
			where	x.nm_usuario_grupo	= a.nm_usuario_exec) or ie_desenv_w = 'N')
	and	(exists	(select	1
			from	usuario_grupo_sup x
			where	x.nm_usuario_grupo	= a.nm_usuario_exec) or ie_suporte_w = 'N');
	exception
	when others then
		nr_seq_exec_w := 0;
	end;


	if	(nvl(nr_seq_exec_w,0) = 0) then
		begin
		select	max(a.nr_sequencia)
		into	nr_seq_exec_w
		from	man_ordem_servico_exec a
		where	a.nr_seq_ordem 	= nr_ordem_servico_p
		and	a.dt_fim_execucao is null
		and  	exists (select	1
			from  	man_estagio_usuario d
			where   d.nm_usuario_acao 	= a.nm_usuario_exec
			and	decode(nvl(d.cd_perfil,0),0,nvl(cd_perfil_ativo_w,0),d.cd_perfil) =
				decode(nvl(cd_perfil_ativo_w,0),0,decode(nvl(d.cd_perfil,0),0,nvl(cd_perfil_ativo_w,0),d.cd_perfil),
				cd_perfil_ativo_w))
		and	(exists	(select	1
				from	usuario_grupo_des x
				where	x.nm_usuario_grupo	= a.nm_usuario_exec) or ie_desenv_w = 'N')
		and	(exists	(select	1
				from	usuario_grupo_sup x
				where	x.nm_usuario_grupo	= a.nm_usuario_exec) or ie_suporte_w = 'N');
		exception
		when others then
			nr_seq_exec_w := 0;
		end;
	end if;



	if	(nr_seq_exec_w = 0) then
		begin
		select	max(a.nr_sequencia)
		into	nr_seq_exec_w
		from	man_tipo_executor c,
			man_ordem_servico_exec a
		where	a.nr_seq_tipo_exec	= c.nr_sequencia
		and	c.ie_tipo_executor 	= '1'
		and	a.nr_seq_ordem 		= nr_ordem_servico_p
		--and	rownum < 2
		and  	exists (select	1
				from  	man_estagio_usuario d
				where   d.nm_usuario_acao 	= a.nm_usuario_exec
				and	decode(nvl(d.cd_perfil,0),0,nvl(cd_perfil_ativo_w,0),d.cd_perfil) =
				decode(nvl(cd_perfil_ativo_w,0),0,decode(nvl(d.cd_perfil,0),0,nvl(cd_perfil_ativo_w,0),d.cd_perfil),cd_perfil_ativo_w))
		and	(exists	(select	1
				from	usuario_grupo_des x
				where	x.nm_usuario_grupo	= a.nm_usuario_exec) or ie_desenv_w = 'N')
		and	(exists	(select	1
				from	usuario_grupo_sup x
				where	x.nm_usuario_grupo	= a.nm_usuario_exec) or ie_suporte_w = 'N');
		exception
		when others then
			nr_seq_exec_w := 0;
		end;
	end if;

	if	(nvl(nr_seq_exec_w,0) = 0) then
		begin
		select	max(a.nr_sequencia)
		into	nr_seq_exec_w
		from	man_ordem_servico_exec a
		where	a.nr_seq_ordem 	= nr_ordem_servico_p
		--and	rownum < 2
		and  	exists (select	1
			from  	man_estagio_usuario d
			where   d.nm_usuario_acao 	= a.nm_usuario_exec
			and	decode(nvl(d.cd_perfil,0),0,nvl(cd_perfil_ativo_w,0),d.cd_perfil) =
				decode(nvl(cd_perfil_ativo_w,0),0,decode(nvl(d.cd_perfil,0),0,nvl(cd_perfil_ativo_w,0),d.cd_perfil),
				cd_perfil_ativo_w))
		and	(exists	(select	1
				from	usuario_grupo_des x
				where	x.nm_usuario_grupo	= a.nm_usuario_exec) or ie_desenv_w = 'N')
		and	(exists	(select	1
				from	usuario_grupo_sup x
				where	x.nm_usuario_grupo	= a.nm_usuario_exec) or ie_suporte_w = 'N');
		exception
		when others then
			nr_seq_exec_w := 0;
		end;
	end if;*/
	if (coalesce(nr_seq_exec_w,0) <> 0) then
		begin
		ds_resultado_w	:= nm_usuario_w;
		end;
	elsif (coalesce(nr_seq_exec_w,0) = 0) then
		begin
		begin
		select	a.nr_sequencia
		into STRICT	nr_seq_exec_w
		from	man_ordem_servico_exec a
		where	a.nr_seq_ordem 	= nr_ordem_servico_p  LIMIT 1;
		exception
		when others then
			nr_seq_exec_w := 0;
		end;

		if (nr_seq_exec_w <> 0) then
			begin
			select	nm_usuario_exec
			into STRICT	nm_usuario_w
			from	man_ordem_servico_exec
			where	nr_seq_ordem 	= nr_ordem_servico_p
			and	nr_sequencia	= nr_seq_exec_w;

			ds_resultado_w	:= nm_usuario_w;
			end;
		else
			ds_resultado_w	:= nm_usuario_exec_w;
		end if;
		end;
	end if;
	end;
end if;

return ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
