SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nome_usuario_exec_des (NR_ORDEM_SERVICO_P bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


nm_usuario_exec_w	varchar(15);
nr_seq_exec_w		bigint;
nm_usuario_w		varchar(80);
ds_resultado_w		varchar(80);
cd_pessoa_fisica_w	varchar(30);
cd_perfil_ativo_w		integer := obter_perfil_ativo;


BEGIN

if (NR_ORDEM_SERVICO_P IS NOT NULL AND NR_ORDEM_SERVICO_P::text <> '') then
	begin
	select coalesce(max(nm_usuario_exec), 'NÃ£o Informado')
	into STRICT	nm_usuario_exec_w
	from	usuario_grupo_des b,
			man_ordem_servico a
	where	a.nr_sequencia 	= nr_ordem_servico_p
	and		a.nm_usuario_exec = b.nm_usuario_grupo;


	select coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_exec_w
	from	usuario_grupo_des c,
			man_ordem_servico_exec a,
		man_ordem_servico b
	where	a.nr_seq_ordem = b.nr_sequencia
	and	a.nr_seq_ordem 	= nr_ordem_servico_p
	and		a.nm_usuario_exec = c.nm_usuario_grupo
	and  	b.nr_seq_estagio in (SELECT nr_seq_estagio
 		from  	man_estagio_usuario
 		where   nm_usuario_acao = a.nm_usuario_exec
		and	CASE WHEN coalesce(cd_perfil,0)=0 THEN coalesce(cd_perfil_ativo_w,0)  ELSE cd_perfil END  =
			CASE WHEN coalesce(cd_perfil_ativo_w,0)=0 THEN CASE WHEN coalesce(cd_perfil,0)=0 THEN coalesce(cd_perfil_ativo_w,0)  ELSE cd_perfil END   ELSE cd_perfil_ativo_w END );


	if (nr_seq_exec_w <> 0) then
		begin
		select nm_usuario_exec
		into STRICT	nm_usuario_w
		from	man_ordem_servico_exec
		where	nr_seq_ordem 	= nr_ordem_servico_p
		and	nr_sequencia	= nr_seq_exec_w;

		ds_resultado_w	:= nm_usuario_w;
		end;
	else

		begin

		select coalesce(max(a.nr_sequencia),0)
		into STRICT	nr_seq_exec_w
		from	usuario_grupo_des c,
			man_ordem_servico_exec a,
			man_ordem_servico b
		where	a.nr_seq_ordem = b.nr_sequencia
		and		a.nm_usuario_exec = c.nm_usuario_grupo
		and	a.nr_seq_ordem 	= nr_ordem_servico_p;

		if (nr_seq_exec_w <> 0) then
			begin
			select nm_usuario_exec
			into STRICT	nm_usuario_w
			from	man_ordem_servico_exec
			where	nr_seq_ordem 	= nr_ordem_servico_p
			and	nr_sequencia	= nr_seq_exec_w;

			ds_resultado_w	:= nm_usuario_w;
			end;
		else
			ds_resultado_w	:= nm_usuario_exec_w;

		end if;
		end;

	end if;
	end;
end if;

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	usuario
where	nm_usuario		= DS_RESULTADO_W;

if (ie_tipo_p		= 'N') then
	RETURN cd_pessoa_fisica_w;
else
	RETURN DS_RESULTADO_W;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
