CREATE OR REPLACE FUNCTION obter_nome_usu_dest_checkup (nm_usuarios_destino_p text) RETURNS varchar AS $body$
DECLARE


nm_usuarios_destino_w	varchar(2000);
ds_usuarios_destino_w	varchar(4000);
nr_pos_virgula_w	bigint;
nm_usuario_w		varchar(15);
ds_usuario_w		varchar(40);
qt_tam_lista_w		bigint;


BEGIN
if (nm_usuarios_destino_p IS NOT NULL AND nm_usuarios_destino_p::text <> '') then
	begin
	nm_usuarios_destino_w := nm_usuarios_destino_p;
	while(nm_usuarios_destino_w IS NOT NULL AND nm_usuarios_destino_w::text <> '') loop
		begin
		nr_pos_virgula_w := position(',' in nm_usuarios_destino_w);
		if (nr_pos_virgula_w > 0) then
			begin
			nm_usuario_w 		:= substr(nm_usuarios_destino_w,1,nr_pos_virgula_w-1);
			nm_usuarios_destino_w	:= substr(nm_usuarios_destino_w,nr_pos_virgula_w+2,length(nm_usuarios_destino_w));
			end;
		else
			begin
			nm_usuario_w		:= null;
			nm_usuarios_destino_w	:= null;
			end;
		end if;

		if (nm_usuario_w IS NOT NULL AND nm_usuario_w::text <> '') then
			begin
			ds_usuario_w 	:= obter_desc_usuario(nm_usuario_w);
			qt_tam_lista_w	:= coalesce(length(ds_usuarios_destino_w),0) + length(ds_usuario_w) + 2;

			if (qt_tam_lista_w < 4000) then
				begin
				ds_usuarios_destino_w := ds_usuarios_destino_w || ds_usuario_w || ', ';
				end;
			end if;
			end;
		end if;
		end;
	end loop;
	end;
end if;
return ds_usuarios_destino_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
