CREATE OR REPLACE FUNCTION obter_nome_veiculo ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(40);


BEGIN

begin
select	coalesce(max(substr(nm_veiculo,1,40)),wheb_mensagem_pck.get_texto(308633)) -- Não informado
into STRICT	ds_retorno_w
from 	eme_veiculo
where 	nr_sequencia	= nr_sequencia_p;
exception
	when others then
	ds_retorno_w:= wheb_mensagem_pck.get_texto(308633); -- Não informado
end;

return ds_retorno_w;

end;
$body$
