CREATE OR REPLACE FUNCTION obter_nome_visitante (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
nm_visitante_w		varchar(255);
			

BEGIN 
 
select	max(substr(coalesce(obter_nome_pf(cd_pessoa_fisica),nm_visitante),1,250)) 
into STRICT	nm_visitante_w 
from	atendimento_visita 
where	nr_sequencia = nr_sequencia_p;
 
return	nm_visitante_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
