SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nom_rc_telefone (cd_pessoa_fisica_p text, cd_cgc_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

								
ds_telefone_w	varchar(50);
nr_ddi_telefone_w	varchar(30);
nr_ddd_telefone_w	varchar(30);
nr_telefone_w		varchar(30);
								

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	/* Find first by celphone */

	select	a.nr_ddi_celular,
			a.nr_ddd_celular,
			a.nr_telefone_celular
	into STRICT	nr_ddi_telefone_w,
			nr_ddd_telefone_w,
			nr_telefone_w
	from	pessoa_fisica a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;
	
	if (coalesce(nr_telefone_w::text, '') = '') then
		select	max(a.nr_ddi_telefone),
				max(a.nr_ddd_telefone),
				max(a.nr_telefone)
		into STRICT	nr_ddi_telefone_w,
				nr_ddd_telefone_w,
				nr_telefone_w
		from	compl_pessoa_fisica a
		where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
		and		ie_tipo_complemento	= 1;
	end if;

elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	select	a.nr_ddi_telefone,
			a.nr_ddd_telefone,
			a.nr_telefone
	into STRICT	nr_ddi_telefone_w,
			nr_ddd_telefone_w,
			nr_telefone_w
	from	pessoa_juridica a
	where	a.cd_cgc = cd_cgc_p;
end if;

if (nr_ddi_telefone_w IS NOT NULL AND nr_ddi_telefone_w::text <> '') and (nr_telefone_w IS NOT NULL AND nr_telefone_w::text <> '') then
	ds_telefone_w	:= '+' || nr_ddi_telefone_w || nr_ddd_telefone_w || nr_telefone_w;
end if;

return ds_telefone_w;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_nom_rc_telefone (cd_pessoa_fisica_p text, cd_cgc_p text, ie_opcao_p text) FROM PUBLIC;

