CREATE OR REPLACE FUNCTION obter_norma_compra_item_solic ( nr_solic_compra_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_seq_regra_w				bigint;
ie_tipo_servico_w				varchar(15);
cd_estabelecimento_w			integer;
cd_perfil_w				integer;
cd_centro_custo_w				integer;
ie_urgente_w				varchar(1);
cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
nr_seq_familia_w				bigint;
ie_padronizado_w				varchar(1);
ie_estoque_w				varchar(1);

c01 CURSOR FOR 
SELECT	a.nr_sequencia 
from	sup_normas_compras a 
where	a.ie_situacao					= 'A' 
and	a.cd_estabelecimento				= cd_estabelecimento_w 
and	coalesce(a.ie_tipo_servico,coalesce(ie_tipo_servico_w, '0'))	= coalesce(ie_tipo_servico_w,'0') 
and	coalesce(cd_centro_custo,coalesce(cd_centro_custo_w, 0))		= coalesce(cd_centro_custo_w,0) 
and	coalesce(cd_perfil,cd_perfil_w)				= cd_perfil_w 
and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w 
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w 
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w 
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0) 
and	((coalesce(nr_seq_familia, nr_seq_familia_w)			= nr_seq_familia_w) or (coalesce(nr_seq_familia::text, '') = '')) 
and	((coalesce(ie_material_estoque,'A') = 'A') or 
	((coalesce(ie_material_estoque,'A') = 'S') and (ie_estoque_w = 'S')) or 
	((coalesce(ie_material_estoque,'A') = 'N') and (ie_estoque_w = 'N'))) 
and	((coalesce(ie_padronizado,'A') = 'A') or 
	((coalesce(ie_padronizado,'A') = 'S') and (ie_padronizado_w = 'S')) or 
	((coalesce(ie_padronizado,'A') = 'N') and (ie_padronizado_w = 'N'))) 
and	((coalesce(ie_urgente,'A') = 'A') or 
	((coalesce(ie_urgente,'A') = 'S') and (ie_urgente_w = 'S')) or 
	((coalesce(ie_urgente,'A') = 'N') and (ie_urgente_w = 'N'))) 
order by 
	coalesce(cd_material,0), 
	coalesce(cd_classe_material,0), 
	coalesce(cd_subgrupo_material,0), 
	coalesce(cd_grupo_material,0), 
	coalesce(nr_seq_familia,0), 
	coalesce(cd_centro_custo,999999) desc, 
	coalesce(cd_perfil,99999) desc;


BEGIN 
 
select	coalesce(ie_tipo_servico,'ES'), 
	cd_estabelecimento, 
	cd_perfil, 
	cd_centro_custo, 
	ie_urgente 
into STRICT	ie_tipo_servico_w, 
	cd_estabelecimento_w, 
	cd_perfil_w, 
	cd_centro_custo_w, 
	ie_urgente_w 
from	solic_compra 
where	nr_solic_compra = nr_solic_compra_p;
 
 
select	a.cd_grupo_material, 
	a.cd_subgrupo_material, 
	a.cd_classe_material, 
	a.nr_seq_familia, 
	b.ie_material_estoque, 
	b.ie_padronizado 
into STRICT	cd_grupo_material_w, 
	cd_subgrupo_material_w, 
	cd_classe_material_w, 
	nr_seq_familia_w, 
	ie_estoque_w, 
	ie_padronizado_w 
from	estrutura_material_v a, 
	material_estab b 
where	a.cd_material = b.cd_material 
and	b.cd_estabelecimento = cd_estabelecimento_w 
and	a.cd_material = cd_material_p;
 
open C01;
loop 
fetch C01 into	 
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	nr_seq_regra_w := nr_seq_regra_w;
	end;
end loop;
close C01;
 
return nr_seq_regra_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
