CREATE OR REPLACE FUNCTION obter_notas_canceladas (cd_operacao_nf_p bigint, cd_natureza_operacao_p bigint, dt_emissao_p timestamp, cd_estabelecimento_p bigint, cd_serie_nf_p text) RETURNS varchar AS $body$
DECLARE


ds_notas_w	varchar(255);
nr_nota_fiscal_w	varchar(255);
c01 CURSOR FOR
	SELECT	distinct nr_nota_fiscal
	from	nota_fiscal
	where	cd_operacao_nf		= cd_operacao_nf_p
	and	cd_natureza_operacao	= coalesce(cd_natureza_operacao_p, cd_natureza_operacao)
	and	ie_situacao in ('2','3','9')
	and	trunc(dt_emissao, 'dd')	= trunc(dt_emissao_p, 'dd')
	and	cd_estabelecimento		= cd_estabelecimento_p
	and	cd_serie_nf		= cd_serie_nf_p
	order by 1;

BEGIN

ds_notas_w	:= '';

open c01;
loop
fetch c01 into
	nr_nota_fiscal_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_notas_w := substr(ds_notas_w || nr_nota_fiscal_w || ', ',1,255);
end loop;
close c01;

ds_notas_w := substr(ds_notas_w,1,length(ds_notas_w) - 1);

return ds_notas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
