CREATE OR REPLACE FUNCTION obter_notas_gif (cd_estabelecimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p text, ie_tipo_gif_p text, cd_cgc_p text, cst_p text, cfps_p text, cnae_p text) RETURNS varchar AS $body$
DECLARE





ds_notas_w		varchar(255);
nr_nota_fiscal_w	varchar(255);

/*
C - Canceladas
A - Abertas
*/
c01 CURSOR FOR
	SELECT	distinct n.nr_nota_fiscal
	from	nota_fiscal_item_trib x,
		nota_fiscal n,
		tributo t,
		situacao_trib_prest_serv s,
		operacao_nota a
	where	x.nr_sequencia = n.nr_sequencia
	and	x.cd_tributo    = t.cd_tributo
	and	x.nr_seq_sit_trib = s.nr_sequencia
	and	a.cd_operacao_nf = n.cd_operacao_nf
	and	t.ie_tipo_tributo = 'ISS'
	and	substr(obter_se_nota_entrada_saida(n.nr_sequencia),1,1) = 'S'
	and	ie_tipo_gif_p = 'SP'
	and	dt_emissao between dt_inicial_p and fim_dia(dt_final_p)
	and	((((n.ie_situacao = '9') or (n.ie_situacao = '3')) and (ie_opcao_p = 'C')) or
		((n.ie_situacao = '1') and (ie_opcao_p = 'A') and (Lpad(substr(obter_cnae_florianopolis(n.cd_estabelecimento,n.nr_sequencia),1, 11),11,'00000000000') = cnae_p) and (lpad(substr(s.cd_situacao,1,3),3,'0') = cst_p) and (obter_cfps_municipio(n.cd_estabelecimento,n.cd_cgc,n.cd_pessoa_fisica,'FLORIANOPOLIS') = cfps_p)  ))
	and	n.cd_cgc_emitente = cd_cgc_p
	and	n.cd_estabelecimento = cd_estabelecimento_p
	and	(n.dt_atualizacao_estoque IS NOT NULL AND n.dt_atualizacao_estoque::text <> '')
	and	a.ie_servico = 'S'
	
union all

	SELECT	distinct n.nr_nota_fiscal
	from	nota_fiscal_item_trib x,
		nota_fiscal n,
		tributo t,
		situacao_trib_prest_serv s,
		operacao_nota a,
		estabelecimento e
	where	x.nr_sequencia = n.nr_sequencia
	and	x.cd_tributo    = t.cd_tributo
	and	x.nr_seq_sit_trib = s.nr_sequencia
	and	a.cd_operacao_nf = n.cd_operacao_nf
	and	t.ie_tipo_tributo = 'ISS'
	and	substr(obter_se_nota_entrada_saida(n.nr_sequencia),1,1) <> 'S'
	and	ie_tipo_gif_p = 'ST'
	and	dt_emissao between dt_inicial_p and fim_dia(dt_final_p)
	and	((((n.ie_situacao = '9') or (n.ie_situacao = '3')) and (ie_opcao_p = 'C')) or
		((n.ie_situacao = '1') and (ie_opcao_p = 'A') and (Lpad(substr(obter_cnae_florianopolis(n.cd_estabelecimento,n.nr_sequencia),1, 11),11,'00000000000') = cnae_p) and (lpad(substr(s.cd_situacao,1,3),3,'0') = cst_p) and (obter_cfps_municipio(n.cd_estabelecimento,n.cd_cgc,n.cd_pessoa_fisica,'FLORIANOPOLIS') = cfps_p)  ))
	and	n.cd_cgc_emitente <> e.cd_cgc
	and	n.cd_estabelecimento = e.cd_estabelecimento
	and	n.cd_estabelecimento = cd_estabelecimento_p
	and	(n.dt_atualizacao_estoque IS NOT NULL AND n.dt_atualizacao_estoque::text <> '')
	and	a.ie_servico = 'S'
	order by 1;


BEGIN

ds_notas_w      := '';

open c01;
loop
fetch c01 into
	nr_nota_fiscal_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_notas_w := substr(ds_notas_w || nr_nota_fiscal_w || ',',1,255);
end loop;
close c01;

ds_notas_w := substr(ds_notas_w,1,length(ds_notas_w) - 1);

return ds_notas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_notas_gif (cd_estabelecimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p text, ie_tipo_gif_p text, cd_cgc_p text, cst_p text, cfps_p text, cnae_p text) FROM PUBLIC;

