CREATE OR REPLACE FUNCTION obter_notas_gps ( nr_seq_gps_p bigint) RETURNS varchar AS $body$
DECLARE


nr_nota_fiscal_w 	varchar(255);
retorno_w 	varchar(2000);
contador_w 	bigint := 0;
qt_registro_w 	bigint;

c01 CURSOR FOR
	SELECT 	n.nr_nota_fiscal
	FROM gps_titulo p, gps g, titulo_pagar t
LEFT OUTER JOIN nota_fiscal n ON (t.nr_seq_nota_fiscal = n.nr_sequencia)
WHERE p.nr_seq_gps = g.nr_sequencia and p.nr_titulo = t.nr_titulo and g.nr_sequencia = nr_seq_gps_p;


BEGIN

select 	count(*)
into STRICT	qt_registro_w
FROM gps_titulo p, gps g, titulo_pagar t
LEFT OUTER JOIN nota_fiscal n ON (t.nr_seq_nota_fiscal = n.nr_sequencia)
WHERE p.nr_seq_gps = g.nr_sequencia and p.nr_titulo = t.nr_titulo and g.nr_sequencia = nr_seq_gps_p;

if (qt_registro_w > 0) then

	open c01;
	loop
	fetch c01 into
		nr_nota_fiscal_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		if (contador_w = 0) then
			retorno_w := nr_nota_fiscal_w;
		elsif (contador_w <> qt_registro_w) then
			retorno_w := retorno_w || ', ' || nr_nota_fiscal_w;
		elsif (contador_w = qt_registro_w) then
			retorno_w := retorno_w || nr_nota_fiscal_w;
		end if;

		contador_w := contador_w + 1;

		end;
	end loop;
	close c01;
end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
