CREATE OR REPLACE FUNCTION obter_nota_fiscal_adiant (nr_adiantamento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w  nota_fiscal.nr_sequencia%type;


BEGIN

select min(nr_sequencia) -- HSJ 2018 -- Alterado para MIN para buscar a nota fiscal de adiantamento e não a nota de crédito do  adiantamento
into STRICT   nr_sequencia_w
from   nota_fiscal
where  nr_seq_adiantamento = nr_adiantamento_p;

return nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
