CREATE OR REPLACE FUNCTION obter_nota_fiscal_caixa_rec ( nr_seq_caixa_rec_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w varchar(255);


BEGIN 
 
	SELECT	max(y.nr_nota_fiscal) 
	into STRICT	ds_retorno_w 
	FROM	titulo_receber y, 
		titulo_receber_liq x 
	WHERE	x.nr_titulo = y.nr_titulo 
	and	(y.nr_nota_fiscal IS NOT NULL AND y.nr_nota_fiscal::text <> '') 
	AND	x.nr_seq_caixa_rec = nr_seq_caixa_rec_p;
	 
	 
	 
	 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
