CREATE OR REPLACE FUNCTION obter_nota_fiscal_oc (nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(500);
nr_nota_w		varchar(15);

c01 CURSOR FOR
SELECT	distinct nr_nota_fiscal
from	nota_fiscal
where	nr_ordem_compra = nr_ordem_compra_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_nota_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_retorno_w := nr_nota_w || ', ' || ds_retorno_w;
	end;
end loop;
close c01;

ds_retorno_w := substr(ds_retorno_w,1,length(ds_retorno_w) - 2);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
