CREATE OR REPLACE FUNCTION obter_nota_fiscal_opm (nr_ordem_opm_p bigint) RETURNS varchar AS $body$
DECLARE


nr_nota_fiscal_c_w		varchar(255);
nr_nota_fiscal_e_w		varchar(255);
nr_nota_fiscal_p_w		varchar(255);
nr_nota_fiscal_final_w	varchar(255);


BEGIN

select 	max(a.nr_nota_fiscal)
into STRICT	nr_nota_fiscal_c_w
from	ordem_producao_opm_nf a,
        nota_fiscal b
where	a.nr_ordem_opm = nr_ordem_opm_p
and     a.nr_nota_fiscal = b.nr_sequencia
and     b.ie_situacao = '1'
and	a.ie_tipo_nota_opm = 'C';

select 	max(a.nr_nota_fiscal)
into STRICT	nr_nota_fiscal_e_w
from	ordem_producao_opm_nf a,
        nota_fiscal b
where	a.nr_ordem_opm = nr_ordem_opm_p
and     a.nr_nota_fiscal = b.nr_sequencia
and     b.ie_situacao = '1'
and	a.ie_tipo_nota_opm = 'E';

select 	max(a.nr_nota_fiscal)
into STRICT	nr_nota_fiscal_p_w
from	ordem_producao_opm_nf a,
        nota_fiscal b
where	a.nr_ordem_opm = nr_ordem_opm_p
and     a.nr_nota_fiscal = b.nr_sequencia
and     b.ie_situacao = '1'
and	a.ie_tipo_nota_opm = 'P';

if (nr_nota_fiscal_c_w IS NOT NULL AND nr_nota_fiscal_c_w::text <> '' AND nr_nota_fiscal_e_w IS NOT NULL AND nr_nota_fiscal_e_w::text <> '') then
	nr_nota_fiscal_c_w := nr_nota_fiscal_c_w || ', ';
end if;

nr_nota_fiscal_final_w := nr_nota_fiscal_c_w || nr_nota_fiscal_e_w || nr_nota_fiscal_p_w;

return	nr_nota_fiscal_final_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
