CREATE OR REPLACE FUNCTION obter_nota_fiscal_repasse ( nr_interno_conta_p bigint, ie_opcao_p text, nr_seq_protocolo_p bigint, nr_seq_lote_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_protocolo_w	bigint := null;
nr_seq_nota_fiscal_w	varchar(255) := null;
nr_seq_lote_protocolo_w	bigint := null;
nr_nota_fiscal_w		varchar(255) := null;
nr_interno_conta_w	bigint := null;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_nota_fiscal_w
from	nota_fiscal
where	nr_interno_conta	= nr_interno_conta_p;

if (coalesce(nr_seq_nota_fiscal_w::text, '') = '') then

	if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_nota_fiscal_w
		from	nota_fiscal
		where	nr_seq_protocolo	= nr_seq_protocolo_p;

		if (coalesce(nr_seq_nota_fiscal_w::text, '') = '') then
			select	max(nr_seq_nf_saida)
			into STRICT	nr_seq_nota_fiscal_w
			from	titulo_receber
			where	nr_seq_protocolo	= nr_seq_protocolo_p;

			if (coalesce(nr_seq_nota_fiscal_w::text, '') = '') then
				if (nr_seq_lote_protocolo_p IS NOT NULL AND nr_seq_lote_protocolo_p::text <> '') then
					select	max(nr_sequencia)
					into STRICT	nr_seq_nota_fiscal_w
					from	nota_fiscal
					where	nr_seq_lote_prot	= nr_seq_lote_protocolo_p;
				end if;

				if (coalesce(nr_seq_nota_fiscal_w::text, '') = '') then
					select	max(nr_interno_conta)
					into STRICT	nr_interno_conta_w
					from	conta_paciente
					where	nr_seq_protocolo	= nr_seq_protocolo_p;

					if (nr_interno_conta_w IS NOT NULL AND nr_interno_conta_w::text <> '') then
						select	max(nr_sequencia)
						into STRICT	nr_seq_nota_fiscal_w
						from	nota_fiscal a
						where	a.nr_interno_conta		= nr_interno_conta_w
						and	a.ie_situacao		= '1';
					end if;
				end if;

			end if;
		end if;


	else
		select	max(nr_seq_nf_saida)
		into STRICT	nr_seq_nota_fiscal_w
		from	titulo_receber
		where	nr_interno_conta	= nr_interno_conta_p;

	end if;
end if;

if (ie_opcao_p = 'N') then
	if (nr_seq_nota_fiscal_w IS NOT NULL AND nr_seq_nota_fiscal_w::text <> '') then
		select	max(nr_nota_fiscal)
		into STRICT	nr_nota_fiscal_w
		from	nota_fiscal
		where	nr_sequencia	= nr_seq_nota_fiscal_w;

		return	nr_nota_fiscal_w;
	end if;
else
	return	nr_seq_nota_fiscal_w;
end if;

return	null;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
