CREATE OR REPLACE FUNCTION obter_novo_numero_controle ( nr_seq_controle_p bigint) RETURNS bigint AS $body$
DECLARE
	-- Change this to reflect the dblink connection string
	v_conn_str  text := format('port=%s dbname=%s user=%s', current_setting('port'), current_database(), current_user);
	v_query     text;

	v_ret	bigint;
BEGIN
	v_query := 'SELECT * FROM obter_novo_numero_controle_atx ( ' || quote_nullable(nr_seq_controle_p) || ' )';
