CREATE OR REPLACE FUNCTION obter_novo_numero_controle_atx ( nr_seq_controle_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_controle_w		bigint;
nr_seq_controle_ww		bigint;
ie_possui_w			varchar(1);
nr_controle_unico_w		varchar(2);
BEGIN
nr_controle_unico_w := Obter_Param_Usuario(8014, 119, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, nr_controle_unico_w);

if (coalesce(nr_seq_controle_p,0) > 0) then

	SELECT	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	INTO STRICT 	ie_possui_w
	FROM	atendimento_visita
	where	nr_seq_controle = nr_seq_controle_p;

	if (ie_possui_w = 'S') then

		if (nr_controle_unico_w = 'S')then
			select	nextval('controle_visita_unico_seq')
			into STRICT	nr_seq_controle_ww
			;
		else
			SELECT	somente_numero(MAX(nr_seq_controle)) + 1
			INTO STRICT 	nr_seq_controle_ww
			FROM	atendimento_visita;
		end if;
		nr_seq_controle_w	:= nr_seq_controle_ww;
	else
		nr_seq_controle_w := nr_seq_controle_p;
	end if;

else
	begin
	if (nr_controle_unico_w = 'S')then
		select	nextval('controle_visita_unico_seq')
		into STRICT	nr_seq_controle_ww
		;
	else
		SELECT	coalesce(MAX(nr_seq_controle),0) + 1
		INTO STRICT 	nr_seq_controle_ww
		FROM	atendimento_visita;
	end if;
	exception
		when others then
		nr_seq_controle_w	:= 999999999999999;
	end;

	nr_seq_controle_w	:= nr_seq_controle_ww;

end if;

return	nr_seq_controle_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
