CREATE OR REPLACE FUNCTION obter_nrseqexame_integracao ( cd_exame_p text, ds_sigla_equip_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_exame_w		bigint;
ie_solicitacao_w	varchar(1);
nr_seq_superior_w 	bigint;




BEGIN

select	coalesce(max(nr_seq_exame),0),
	coalesce(max(ie_solicitacao),''),
	coalesce(max(nr_seq_superior),0)
into STRICT	nr_seq_exame_w,
	ie_solicitacao_w,
	nr_seq_superior_w
from    exame_laboratorio
where 	Obter_Equip_Exame_integracao(cd_exame_p,ds_sigla_equip_p ,1) = nr_seq_exame;

if (nr_seq_exame_w = 0) then
	select	coalesce(max(nr_seq_exame),0),
		coalesce(max(ie_solicitacao),''),
		coalesce(max(nr_seq_superior),0)
	into STRICT	nr_seq_exame_w,
		ie_solicitacao_w,
		nr_seq_superior_w
	from    exame_laboratorio
	where coalesce(cd_exame_integracao, cd_exame) = cd_exame_p;
end if;

if (ie_solicitacao_w = 'N') and (nr_seq_superior_w <> 0) then
   nr_seq_exame_w := nr_seq_superior_w;
end if;

return	nr_seq_exame_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
