SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nr_acesso_dicom ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_propaci_p bigint) RETURNS varchar AS $body$
DECLARE


nr_acesso_dicom_w	varchar(20);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then

  select  coalesce(max(nr_acesso_dicom),0)
  into STRICT    nr_acesso_dicom_w
  from    prescr_procedimento
  where    nr_prescricao  = nr_prescricao_p
  and      nr_sequencia  = nr_seq_prescr_p;

-- Trata cirurgia
elsif (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p is     null) then

  select  coalesce(max(nr_acesso_dicom),0)
  into STRICT    nr_acesso_dicom_w
  from    cirurgia_carestream c
  where    c.nr_prescricao  = nr_prescricao_p;
-- fim do trata cirurgia
elsif (nr_seq_propaci_p IS NOT NULL AND nr_seq_propaci_p::text <> '') then

  select  coalesce(max(b.nr_acesso_dicom),0)
  into STRICT    nr_acesso_dicom_w
  from    prescr_procedimento b,
          procedimento_paciente a
  where   b.nr_prescricao  = a.nr_prescricao
  and     b.nr_sequencia  = a.nr_sequencia_prescricao
  and     a.nr_sequencia  = nr_seq_propaci_p;
end if;

return nr_acesso_dicom_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
