CREATE OR REPLACE FUNCTION obter_nr_aidf_nota_fiscal ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


nr_nota_fiscal_w		varchar(255);
cd_serie_nf_w			nota_fiscal.cd_serie_nf%type;
cd_estabelecimento_w	smallint;
nr_aidf_w				varchar(20);


BEGIN

/*Utilizada para buscar o numero da autorizacao de impress√£o de documento fiscal - AIDF da nota fiscal*/

select	nr_nota_fiscal,
	cd_estabelecimento,
	cd_serie_nf
into STRICT	nr_nota_fiscal_w,
	cd_estabelecimento_w,
	cd_serie_nf_w
from	nota_fiscal
where	nr_sequencia = nr_sequencia_p;

select	max(nr_id_dief)
into STRICT	nr_aidf_w
from	nota_fiscal_aidf
where	cd_serie_nf = cd_serie_nf_w
and	(nr_nota_fiscal_w)::numeric  between nr_nota_ini and nr_nota_fim;

return	nr_aidf_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
