CREATE OR REPLACE FUNCTION obter_nr_apac_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_apac_w	bigint;
nr_todos_apac_w	varchar(255);

			 
C01 CURSOR FOR 
	SELECT	nr_apac 
	from	sus_laudo_paciente 
	where	nr_atendimento = nr_atendimento_p;
	

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_apac_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (nr_apac_w IS NOT NULL AND nr_apac_w::text <> '') then 
		if (coalesce(nr_todos_apac_w::text, '') = '') then 
			nr_todos_apac_w := nr_apac_w;
		else 
			nr_todos_apac_w := nr_todos_apac_w ||'/'|| nr_apac_w;
		end if;
	end if;
	end;
end loop;
close C01;
 
 
return	nr_todos_apac_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
