CREATE OR REPLACE FUNCTION obter_nr_ato_ipasgo_mat (nr_seq_proc_princ_p material_atend_paciente.nr_seq_proc_princ%type, nr_interno_conta_p conta_paciente.nr_interno_conta%type, ie_opcao_p text, nm_usuario_p text, dt_mesano_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_ato_ipasgo_w		procedimento_paciente.nr_ato_ipasgo%type	:= 0;
cd_procedimento_w	procedimento_paciente.cd_procedimento%type	:= 0;
ie_origem_proced_w	procedimento_paciente.ie_origem_proced%type	:= 0;
dt_procedimento_w	      	procedimento_paciente.dt_procedimento%type;


BEGIN

if (coalesce(nr_seq_proc_princ_p,0) > 0) then
	begin	
		
	begin
	select	coalesce(x.nr_ato_ipasgo,0)
	into STRICT	nr_ato_ipasgo_w
	from	procedimento_paciente x
	where	x.nr_sequencia = nr_seq_proc_princ_p;	
	exception
	when others then
		nr_ato_ipasgo_w := 0;
	end;
		
		
	if (ie_opcao_p = 'S') and (nr_ato_ipasgo_w = 0) then
		begin
		
		begin
		select 	cd_procedimento,
			ie_origem_proced,
			dt_procedimento
		into STRICT 	cd_procedimento_w,
			ie_origem_proced_w,
			dt_procedimento_w
		from 	procedimento_paciente
		where 	nr_sequencia = nr_seq_proc_princ_p
		and	nr_interno_conta = nr_interno_conta_p;	
		exception
		when others then
			cd_procedimento_w	:= 0;
			ie_origem_proced_w	:= 0;
		end;

		begin
		select 	nr_linha_ato
		into STRICT	nr_ato_ipasgo_w
		from 	w_ipasgo_dados_proc_trat
		where	cd_procedimento = cd_procedimento_w
		and	ie_origem_proced = ie_origem_proced_w
		and	nr_interno_conta = nr_interno_conta_p
		and	nm_usuario	= nm_usuario_p
		and	trunc(dt_mesano_referencia,'mm') = trunc(dt_mesano_referencia_p,'mm')
		and	dt_exec_proc_conta = dt_procedimento_w;
		exception
		when others then
			nr_ato_ipasgo_w	:= 0;
		end;
		
		end;
	end if;
	end;
else
	nr_ato_ipasgo_w := 0;
end if;

return	nr_ato_ipasgo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
