CREATE OR REPLACE FUNCTION obter_nr_autor_prorrog (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


nr_atendimento_w	bigint;
dt_referencia_w		timestamp;
qt_prorrogacao_w	integer;
ie_tipo_autorizacao_w	varchar(2);
ds_retorno_w		varchar(255):= '';


BEGIN

select	coalesce(dt_inicio_vigencia,dt_autorizacao),
	nr_atendimento,
	ie_tipo_autorizacao
into STRICT	dt_referencia_w,
	nr_atendimento_w,
	ie_tipo_autorizacao_w
from	autorizacao_convenio
where	nr_sequencia	= nr_sequencia_p;

select	count(*)
into STRICT	qt_prorrogacao_w
from	autorizacao_convenio a
where	a.ie_tipo_autorizacao	= '2'
and	a.nr_atendimento	= nr_atendimento_w
and	coalesce(a.dt_inicio_vigencia,a.dt_autorizacao) < dt_referencia_w;

if (ie_tipo_autorizacao_w = '2') then
	ds_retorno_w	:= (qt_prorrogacao_w + 1) || 'Âª';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
