SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nr_cel_int (nr_ddi_p text, nr_telefone_p text) RETURNS varchar AS $body$
DECLARE

			
ds_retorno_w 	varchar(255);
nr_ddi_w		agenda_lista_espera.nr_telefone%type;
nr_telefone_w	agenda_lista_espera.nr_telefone%type;


BEGIN

if (nr_telefone_p IS NOT NULL AND nr_telefone_p::text <> '') then

	nr_telefone_w := nr_telefone_p;

	if (substr(nr_ddi_p,1,1) = '+') then
		nr_ddi_w := nr_ddi_p;
	else
		nr_ddi_w := '+' || nr_ddi_p; 	
	end if;
	
	if (nr_ddi_w = '+81' and length(nr_telefone_w) = 9) then
		nr_telefone_w   := '0' || nr_telefone_w;
	end if;

	select	CASE WHEN coalesce(nr_ddi_p::text, '') = '' THEN  nr_telefone_w  ELSE nr_ddi_w || ' ' || nr_telefone_w END
	into STRICT	ds_retorno_w
	;

end if;	
	
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
