CREATE OR REPLACE FUNCTION obter_nr_cheque_tit_pagar (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_cheque_w		bigint;
ds_cheques_w		varchar(254);
nr_bordero_w		numeric(20);

c01 CURSOR FOR 
	SELECT	distinct 
		b.nr_sequencia 
	from	cheque b, 
		CHEQUE_BORDERO_TITULO a 
	where	a.nr_seq_cheque	= b.nr_sequencia 
	and	coalesce(b.dt_cancelamento::text, '') = '' 
	and	a.nr_titulo	= nr_titulo_p;

c02 CURSOR FOR 
	SELECT	distinct 
		b.nr_sequencia 
	from	cheque b, 
		CHEQUE_BORDERO_TITULO a 
	where	a.nr_seq_cheque	= b.nr_sequencia 
	and	coalesce(b.dt_cancelamento::text, '') = '' 
	and	a.nr_bordero	= nr_bordero_w;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_cheque_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	if (length(ds_cheques_w || nr_cheque_w) < 250) then 
		if (ds_cheques_w IS NOT NULL AND ds_cheques_w::text <> '') then 
			ds_cheques_w := ds_cheques_w || ',';
		end if;
		ds_cheques_w := substr(ds_cheques_w || nr_cheque_w,1,254);
	end if;	
 
end loop;
close c01;
 
if (coalesce(ds_cheques_w::text, '') = '') then 
 
	select	coalesce(max(nr_bordero),0) 
	into STRICT	nr_bordero_w 
	from	titulo_pagar_bordero_v 
	where	nr_titulo = nr_titulo_p;
 
	open c02;
	loop 
	fetch c02 into 
			nr_cheque_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		if (length(ds_cheques_w || nr_cheque_w) < 250) then 
			if (ds_cheques_w IS NOT NULL AND ds_cheques_w::text <> '') then 
				ds_cheques_w := ds_cheques_w || ',';
			end if;
 
			ds_cheques_w := substr(ds_cheques_w || nr_cheque_w,1,254);
		end if;
	end loop;
	close c02;
 
end if;
 
return	substr(ds_cheques_w,1,254);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
