CREATE OR REPLACE FUNCTION obter_nr_comunic_setor ( nr_sequencia_p bigint, nr_comunic_setor_p bigint default null) RETURNS varchar AS $body$
DECLARE


retorno_w		varchar(255);


BEGIN

if (nr_comunic_setor_p IS NOT NULL AND nr_comunic_setor_p::text <> '') then
	return nr_comunic_setor_p || ' - ' || obter_ds_setor_atendimento(nr_sequencia_p);
end if;

select	c.nr_comunic_setor || ' - ' || s.ds_setor_atendimento
into STRICT	retorno_w
from	comunic_interna c,
	setor_atendimento s
where	c.cd_setor_origem = s.cd_setor_atendimento
and	c.nr_sequencia = nr_sequencia_p;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
