CREATE OR REPLACE FUNCTION obter_nr_conta_conv_atual (cd_convenio_p bigint, dt_mesano_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_retorno_w		bigint := 0;


BEGIN

begin
select	coalesce(nr_conta_atual,nr_conta_inicial)
into STRICT	nr_retorno_w
from	conta_convenio_atual
where	cd_convenio = cd_convenio_p
and 	trunc(dt_competencia,'mm') = trunc(dt_mesano_referencia_p,'mm')
and	ie_situacao = 'A';
exception
when others then
	nr_retorno_w := 0;
end;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
