CREATE OR REPLACE FUNCTION obter_nr_copias_laudo_pac (nr_atendimento_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

			 
vl_retorno_w		   integer;
nr_copias_w		     integer := 0;
nr_seq_unid_int_w		 integer;
cd_convenio_w		   integer;
ie_tipo_atendimento_w	 smallint;
copias_laudo_w	 	   integer;
copias_laudo_internado_w integer;
		

BEGIN				 
		 
 IF (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') THEN 
 BEGIN 
  SELECT nr_seq_unid_int, 
		  cd_convenio, 
		  ie_tipo_atendimento		 
	INTO STRICT  nr_seq_unid_int_w, 
		  cd_convenio_w, 
		  ie_tipo_atendimento_w		 
	FROM  atendimento_paciente_v 
	WHERE nr_atendimento = nr_atendimento_p;
	 
	copias_laudo_w := obter_param_usuario(28, 1, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, copias_laudo_w);
	copias_laudo_internado_w := obter_param_usuario(28, 17, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, copias_laudo_internado_w);
	 
	IF (coalesce(nr_seq_unid_int_w::text, '') = '') THEN 
	BEGIN 
	 
	 nr_copias_w := obter_relat_regra_copia(cd_convenio_w, cd_setor_atendimento_p, ie_tipo_atendimento_w, cd_estabelecimento_p);
		 
	 IF (coalesce(nr_copias_w::text, '') = '') THEN 
	 BEGIN 
	  nr_copias_w := copias_laudo_w;
	 END;
	 END IF;
	END;
	ELSE 
	BEGIN 
	 nr_copias_w := copias_laudo_internado_w;
	END;
	END IF;
 END;
 END IF;
 
 vl_retorno_w := nr_copias_w;
 
 RETURN vl_retorno_w;
 
END;
$body$
