CREATE OR REPLACE FUNCTION obter_nr_disab_cert (cd_pessoa_fisica_p atendimento_paciente.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


ds_deficiencia_w	varchar(250);
ds_retorno_w		varchar(32000);

C01 CURSOR FOR
	SELECT  b.ds_deficiencia||': '||a.cd_certificado_def
	from  	pf_tipo_deficiencia a,
		tipo_deficiencia b
	where  	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and  	b.nr_sequencia = a.nr_seq_tipo_def
	and  	coalesce(a.IE_SITUACAO,'A') = 'A'
	and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    and (a.cd_certificado_def IS NOT NULL AND a.cd_certificado_def::text <> '');



BEGIN

open c01;
loop
fetch c01 into
		ds_deficiencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := ds_deficiencia_w;
	else
		ds_retorno_w := ds_retorno_w || ', ' || ds_deficiencia_w;
	end if;
	end;
end loop;
close c01;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
