CREATE OR REPLACE FUNCTION obter_nr_doc_processo_aprov ( nr_seq_aprovacao_p bigint, ie_tipo_doc_p text) RETURNS bigint AS $body$
DECLARE

				
/* ie_tipo_doc_p
O - Ordem compra
S - Solicitacao compra
C - Cotacao de compra
R - Requisicao materiais 
N - Nota Fiscal
L - Registro licitacao
G - Contrato
E - Projetos e recursos externos*/
nr_documento_w		bigint;


BEGIN

if (ie_tipo_doc_p = 'O') then
	select	max(a.nr_ordem_compra)
	into STRICT	nr_documento_w
	from	ordem_compra_item a,
		processo_aprov_compra b
	where	a.nr_seq_aprovacao = b.nr_sequencia
	and	b.nr_sequencia = nr_seq_aprovacao_p;
elsif (ie_tipo_doc_p = 'S') then
	select	max(a.nr_solic_compra)
	into STRICT	nr_documento_w
	from	solic_compra_item a,
		processo_aprov_compra b
	where	a.nr_seq_aprovacao = b.nr_sequencia
	and	b.nr_sequencia = nr_seq_aprovacao_p;
elsif (ie_tipo_doc_p = 'N') then
	select	max(a.nr_sequencia)
	into STRICT	nr_documento_w
	from	nota_fiscal_item a,
		processo_aprov_compra b
	where	a.nr_seq_aprovacao = b.nr_sequencia
	and	b.nr_sequencia = nr_seq_aprovacao_p;	
elsif (ie_tipo_doc_p = 'R') then
	select	max(a.nr_requisicao)
	into STRICT	nr_documento_w
	from	item_requisicao_material a,
		processo_aprov_compra b
	where	a.nr_seq_aprovacao = b.nr_sequencia
	and	b.nr_sequencia = nr_seq_aprovacao_p;
elsif (ie_tipo_doc_p = 'C') then
	select	max(a.nr_cot_compra)
	into STRICT	nr_documento_w
	from	cot_compra_item a,
		processo_aprov_compra b
	where	a.nr_seq_aprovacao = b.nr_sequencia
	and	b.nr_sequencia = nr_seq_aprovacao_p;
elsif (ie_tipo_doc_p = 'L') then
	select	max(a.nr_seq_licitacao)
	into STRICT	nr_documento_w
	from	reg_lic_item a,
		processo_aprov_compra b
	where	a.nr_seq_aprovacao = b.nr_sequencia
	and	b.nr_sequencia = nr_seq_aprovacao_p;
elsif (ie_tipo_doc_p = 'G') then
	select	max(a.nr_sequencia)
	into STRICT	nr_documento_w
	from	contrato a,
		processo_aprov_compra b
	where	a.nr_seq_aprovacao = b.nr_sequencia
	and	b.nr_sequencia = nr_seq_aprovacao_p;
elsif (ie_tipo_doc_p = 'E') then
	select	max(a.nr_sequencia)
	into STRICT	nr_documento_w
	from	projeto_recurso a,
		processo_aprov_compra b
	where	a.nr_seq_aprovacao = b.nr_sequencia
	and	b.nr_sequencia = nr_seq_aprovacao_p;
end if;

return	nr_documento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_nr_doc_processo_aprov ( nr_seq_aprovacao_p bigint, ie_tipo_doc_p text) FROM PUBLIC;

