CREATE OR REPLACE FUNCTION obter_nr_etapas_md ( qt_tempo_infusao_p bigint, ie_arred_etapas_p text, qt_hora_fase_p bigint, ie_utiliza_quantidade_p text, ie_agora_p text, ie_operacao_p text, qt_operacao_p bigint, ie_considera_operacao_p char ) RETURNS bigint AS $body$
DECLARE

    nr_etapas_w bigint;

BEGIN
	nr_etapas_w	:= dividir_md(qt_tempo_infusao_p,qt_hora_fase_p);

	if (ie_utiliza_quantidade_p = 'S') and (ie_agora_p = 'S') and (ie_operacao_p = 'X') and (ie_considera_operacao_p = 'S') then
		nr_etapas_w := qt_operacao_p;
	elsif (ie_arred_etapas_p = 'S')	 then
		nr_etapas_w	:= ceil(nr_etapas_w);
	else
		nr_etapas_w	:= trunc(nr_etapas_w);
	end if;
    return nr_etapas_w;
end;
