SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nr_nf_titulo_pagar (nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	bigint := 0;


BEGIN

IF (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') THEN
	SELECT	MAX(b.nr_nota_fiscal)
	INTO STRICT	ds_retorno_w
	FROM	titulo_pagar a,
		nota_fiscal b
	WHERE	nr_titulo = nr_titulo_p
	and	a.nr_seq_nota_fiscal = b.nr_sequencia;
END IF;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
