SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nr_prescr_etq_sol (nr_ordem_manipulacao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_max_prescr_w		prescr_medica.nr_prescricao%type;
nr_prescricao_w		prescr_medica.nr_prescricao%type;


BEGIN

select	max(nr_prescricao)
into STRICT	nr_max_prescr_w
from	prescr_medica
where	dt_prescricao > clock_timestamp() - interval '7 days';

select	coalesce(max(nr_prescricao),0)
into STRICT	nr_prescricao_w
from	prescr_medica
where	nr_prescricao = substr(nr_ordem_manipulacao_p,1,length(nr_max_prescr_w))
or		nr_prescricao = substr(nr_ordem_manipulacao_p,1,length(nr_max_prescr_w)-1);

return	nr_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
