CREATE OR REPLACE FUNCTION obter_nr_pront_bo (cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		bigint := null;
VarRegraProntuario_w	varchar(255);


BEGIN
VarRegraProntuario_w := obter_param_usuario(0, 120, obter_perfil_Ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, VarRegraProntuario_w);
if (VarRegraProntuario_w = 'BASE') or (VarRegraProntuario_w = 'NUNCA') then
	begin
	select		nr_prontuario
	into STRICT		ds_retorno_w
	from		pessoa_fisica
	where		cd_pessoa_fisica = cd_pessoa_fisica_p;
	end;
elsif (VarRegraProntuario_w = 'ESTAB') then
	ds_retorno_w := substr(obter_prontuario_pf(wheb_usuario_pck.get_cd_estabelecimento,cd_pessoa_fisica_p),1,255);
end if;

return	ds_retorno_w;

end;
