nr_seq_result_w	bigint := 0;


BEGIN
if (qt_pontos_p IS NOT NULL AND qt_pontos_p::text <> '') then
	begin

	select 	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_result_w
