CREATE OR REPLACE FUNCTION obter_nr_same_paciente ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w	varchar(255);
nr_sequencia_w	bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	prontuario_estab
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	order by	nr_sequencia;

BEGIN

open c01;
	loop
	fetch c01 into
		nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		ds_resultado_w := ds_resultado_w || nr_sequencia_w || ', ';
	end loop;
close c01;

return substr(ds_resultado_w,1,length(ds_resultado_w) - 2);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
