SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_nr_sequencia_guia (cd_guia_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_tipo
  M - guia manual
  C - cd guia
  P - cd prestador
*/
ds_retorno_w			varchar(20);


BEGIN

if (ie_tipo_p = 'P') then
	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	pls_guia_plano
	where	cd_guia_prestador = cd_guia_p;

elsif (ie_tipo_p = 'M') then
	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	pls_guia_plano
	where	cd_guia_manual = cd_guia_p;
elsif (ie_tipo_p = 'C') then
	select	max(nr_sequencia)
	into STRICT	ds_retorno_w
	from	pls_guia_plano
	where	cd_guia = cd_guia_p;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
